INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (24, 19, 'package ${g.codePackage}.dao;\n\nimport com.baomidou.mybatisplus.mapper.BaseMapper;\nimport com.baomidou.mybatisplus.mapper.Wrapper;\nimport com.baomidou.mybatisplus.plugins.pagination.Pagination;\nimport ${g.codePackage}.model.${g.entityName}Model;\n\nimport org.apache.ibatis.annotations.Param;\nimport org.springframework.stereotype.Repository;\n\nimport java.util.List;\n\n/**\n * ${g.entityName}Dao\n *\n * @author ${g.author}\n * @Date ${g.date}\n */\n@Repository\npublic interface ${g.entityName}Dao extends BaseMapper<${g.entityName}Model>{\n\n    /**\n     * 统计数量\n     * @param model\n     * @return\n     */\n    Integer selectCount(@Param(\"e\")${g.entityName}Model model);\n\n    /**\n     * 查询列表\n     * @param model\n     * @return\n     */\n    List<${g.entityName}Model> selectList(@Param(\"e\")${g.entityName}Model model);\n\n    /**\n     * 分页查询信息\n     * @param pagination\n     * @param model\n     * @param wrapper\n     * @return\n     */\n    List<${g.entityName}Model> selectPage(@Param(\"p\")Pagination pagination,@Param(\"e\") ${g.entityName}Model model,@Param(\"w\") Wrapper<${g.entityName}Model> wrapper);\n\n}\n', 'Java', '2018-01-26 21:56:24', NULL);
INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (25, 20, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!DOCTYPE mapper PUBLIC \"-//mybatis.org//DTD Mapper 3.0//EN\" \"http://mybatis.org/dtd/mybatis-3-mapper.dtd\">\n<mapper namespace=\"${g.codePackage}.dao.${g.entityName}Dao\">\n\n	<resultMap id=\"BaseResultMap\" type=\"${g.codePackage}.model.${g.entityName}Model\">\n		<%for(field in t.fields){%>\n		    <result column=\"${field.fieldName}\" property=\"${field.name}\" />\n		<%}%>\n	</resultMap>\n\n	\n	<select id=\"selectCount\" resultMap=\"BaseResultMap\">\n			select count(1) from ${strutil.toUpperCase(g.tableName)} t\n		<where>\n			<include refid=\"select_content\"/>\n		</where>\n	</select>\n	\n	<select id=\"selectList\" resultMap=\"BaseResultMap\">\n			select * from ${strutil.toUpperCase(g.tableName)} t\n		<where>\n			<include refid=\"select_content\"/>\n		</where>\n	</select>\n\n	<select id=\"selectPage\" resultMap=\"BaseResultMap\">\n			select * from ${strutil.toUpperCase(g.tableName)} t\n		<where>\n			<include refid=\"select_content\"/>\n			\\${w.sqlSegment}\n		</where>\n	</select>\n	\n	<sql id=\"select_content\">	\n			<%for(field in t.fields){%>\n				<if test=\"e.${field.name} != null and e.${field.name} != \'\' \">\n					AND t.${strutil.toUpperCase(field.fieldName)} = #{e.${field.name}}\n				</if>\n			<%}%>\n	</sql>\n\n</mapper>', 'Xml', '2018-01-26 21:56:24', NULL);
INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (26, 21, '@layout(\"/common/_container.html\"){\n<div class=\"row\">\n    <div class=\"col-sm-12\">\n        <div class=\"ibox float-e-margins\">\n            <div class=\"ibox-title\">\n                <h5>${g.name}管理</h5>\n            </div>\n            <div class=\"ibox-content\">\n                <div class=\"row row-lg\">\n                    <div class=\"col-sm-12\">\n                        <div class=\"row\">\n                            <div class=\"col-sm-3\">\n                                <${\"#\"}NameCon id=\"condition\" name=\"名称\" />\n                            </div>\n                            <div class=\"col-sm-3\">\n                                <${\"#\"}button name=\"搜索\" icon=\"fa-search\" clickFun=\"${g.entityName}.search()\"/>\n                            </div>\n                        </div>\n                        <div class=\"hidden-xs\" id=\"${g.entityName}TableToolbar\" role=\"group\">\n                            @if(shiro.hasPermission(\"/${strutil.toLowerCase(g.entityName)}/add\")){\n                                <${\"#\"}button name=\"添加\" icon=\"fa-plus\" clickFun=\"${g.entityName}.openAdd${g.entityName}()\"/>\n                            @}\n                            @if(shiro.hasPermission(\"/${strutil.toLowerCase(g.entityName)}/update\")){\n                                <${\"#\"}button name=\"修改\" icon=\"fa-plus\" clickFun=\"${g.entityName}.open${g.entityName}Detail()\" space=\"true\"/>\n                            @}\n                            @if(shiro.hasPermission(\"/${strutil.toLowerCase(g.entityName)}/delete\")){\n                                <${\"#\"}button name=\"删除\" icon=\"fa-plus\" clickFun=\"${g.entityName}.delete()\" space=\"true\"/>\n                            @}\n                        </div>\n                        <${\"#\"}table id=\"${g.entityName}Table\"/>\n                    </div>\n                </div>\n            </div>\n        </div>\n    </div>\n</div>\n<script  src=\"\\${ctxPath}/static/modular/biz/${strutil.toLowerCase(g.entityName)}/${strutil.toLowerCase(g.entityName)}.js\"></script >\n@}\n', 'Html', '2018-01-26 21:56:24', NULL);
INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (27, 22, '/**\n * ${g.name}管理初始化\n */\nvar ${g.entityName} = {\n    id: \"${g.entityName}Table\",	//表格id\n    seItem: null,		//选中的条目\n    table: null,\n    layerIndex: -1\n};\n\n/**\n * 初始化表格的列\n */\n${g.entityName}.initColumn = function () {\n    return [\n        {field: \'selectItem\', radio: true},\n	<%for(field in t.fields){%>\n	     {title: \'${field.chinaName}\', field: \'${field.name}\', align: \'center\', valign: \'middle\'},\n	<%}%>\n    ];\n};\n\n/**\n * 检查是否选中\n */\n${g.entityName}.check = function () {\n    var selected = $(\'#\' + this.id).bootstrapTable(\'getSelections\');\n    if(selected.length == 0){\n        Feng.info(\"请先选中表格中的某一记录！\");\n        return false;\n    }else{\n        ${g.entityName}.seItem = selected[0];\n        return true;\n    }\n};\n\n/**\n * 点击添加${g.name}\n */\n${g.entityName}.openAdd${g.entityName} = function () {\n    var index = layer.open({\n        type: 2,\n        title: \'添加${g.name}\',\n        area: [\'800px\', \'420px\'], //宽高\n        fix: false, //不固定\n        maxmin: true,\n        content: Feng.ctxPath + \'/${strutil.toLowerCase(g.entityName)}/goto_add\'\n    });\n    this.layerIndex = index;\n};\n\n/**\n * 打开查看${g.name}详情\n */\n${g.entityName}.open${g.entityName}Detail = function () {\n    if (this.check()) {\n        var index = layer.open({\n            type: 2,\n            title: \'${g.name}详情\',\n            area: [\'800px\', \'420px\'], //宽高\n            fix: false, //不固定\n            maxmin: true,\n            content: Feng.ctxPath + \'/${strutil.toLowerCase(g.entityName)}/goto_update/\' + ${g.entityName}.seItem.id\n        });\n        this.layerIndex = index;\n    }\n};\n\n/**\n * 删除${g.name}\n */\n${g.entityName}.delete = function () {\n    if (this.check()) {\n        var ajax = new $ax(Feng.ctxPath + \"/${strutil.toLowerCase(g.entityName)}/delete\", function (data) {\n            Feng.success(\"删除成功!\");\n            ${g.entityName}.table.refresh();\n        }, function (data) {\n            Feng.error(\"删除失败!\" + data.responseJSON.message + \"!\");\n        });\n        ajax.set(\"${strutil.toLowerCase(g.entityName)}Id\",this.seItem.id);\n        ajax.start();\n    }\n};\n\n${g.entityName}.formParams = function() {\n    var queryData = {};\n    return queryData;\n};\n\n/**\n * 查询${g.name}列表\n */\n${g.entityName}.search = function () {\n    var queryData = {};\n    queryData[\'condition\'] = $(\"#condition\").val();\n    ${g.entityName}.table.refresh({query: queryData});\n};\n\n$(function () {\n    var defaultColunms = ${g.entityName}.initColumn();\n    var table = new BSTable(${g.entityName}.id, \"/${strutil.toLowerCase(g.entityName)}/list\", defaultColunms);\n    table.setPaginationType(\"server\");\n    table.setQueryParams(${g.entityName}.formParams());\n    ${g.entityName}.table = table.init();\n});\n', 'JavaScript', '2018-01-26 21:56:24', NULL);
