INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (137, 132, 'import request from \'@/utils/request\';\n\n\n// 获取单个${g.name}\nexport async function get${g.entityName}(id: string) {\n  return request(\'/${g.codePackage}/${g.entityName}s/\\${id}\', {\n    method: \'GET\',\n  });\n}\n\n\n// 获取${g.name}列表\nexport async function list${g.entityName}() {\n  return request(\'/${g.codePackage}/${g.entityName}s\', {\n    method: \'GET\',\n    data: params,\n  });\n}\n\n\n// 新增${g.name}\nexport async function add${g.entityName}(params: any) {\n  return request(\'/${g.codePackage}/${g.entityName}s\', {\n    method: \'POST\',\n    data: params,\n  });\n}\n\n// 修改${g.name}\nexport async function update${g.entityName}(params: any) {\n  return request(\'/${g.codePackage}/${g.entityName}s\', {\n    method: \'PUT\',\n    data: params,\n  });\n}\n\n\n// 删除${g.name}\nexport async function delete${g.entityName}(id: string) {\n  return request(\'/${g.codePackage}/${g.entityName}s/\\${id}\', {\n    method: \'DELETE\',\n  });\n}', 'Java', '2023-02-06 15:34:08', NULL);
INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (138, 133, 'import { PlusOutlined } from \'@ant-design/icons\';\nimport type { ProColumns } from \'@ant-design/pro-components\';\nimport { ProTable } from \'@ant-design/pro-components\';\nimport { Button } from \'antd\';\n\ntype ${g.entityName}Item = {\n	<%for(field in t.fields){%>\n		${field.name}: ${field.type},\n	<%}%>\n};\n\nconst columns: ProColumns<${g.entityName}Item>[] = [\n	<%for(field in t.fields){%>\n		 {\n			title: \'${field.chinaName}\',\n			dataIndex: \'${field.name}\',\n		  },\n	<%}%>\n];\n\nexport default () => (\n  <ProTable<GithubIssueItem>\n    columns={columns}\n    request={async () => {\n      return {\n        data: [\n          {\n            key: 1,\n            name: `TradeCode ${1}`,\n            createdAt: 1602572994055,\n          },\n        ],\n        success: true,\n      };\n    }}\n    rowKey=\"key\"\n    dateFormatter=\"string\"\n    headerTitle=\"${g.name}\"\n    search={{\n      defaultCollapsed: false,\n      labelWidth: \'auto\',\n      optionRender: (searchConfig, formProps, dom) => [\n        ...dom.reverse(),\n        <Button key=\"out\" onClick={() => {}}>\n          导出\n        </Button>,\n      ],\n    }}\n    toolBarRender={() => [\n      <Button key=\"primary\" type=\"primary\">\n        <PlusOutlined />\n        新建\n      </Button>,\n    ]}\n  />\n);', 'Java', '2023-02-06 15:34:12', NULL);
