INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (158, 153, 'import { PlusOutlined, ExclamationCircleFilled } from \'@ant-design/icons\';\nimport type {\n  ActionType,\n  ProColumns,\n  ProDescriptionsItemProps,\n  ProFormInstance,\n} from \'@ant-design/pro-components\';\nimport {\n  ProDescriptions,\n  ProFormText,\n  PageContainer,\n  ProTable,\n} from \'@ant-design/pro-components\';\nimport { ModalForm, DrawerForm, ProFormRadio,ProForm,ProFormSwitch,ProFormDatePicker, } from \'@ant-design/pro-form\';\nimport { Button, Drawer, message, Modal,Form } from \'antd\';\nimport React, { useRef, useState } from \'react\';\nimport { useModel } from \'umi\';\nimport type { API } from \'@/services/api/typings\';\nimport { useAccess } from \'umi\';\nimport request from \'@/utils/request\';\nimport CommonRadio from \'@/components/Form/CommonRadio\';\nimport CommonSelect from \'@/components/Form/CommonSelect\';\n\n\n \n\nexport default () => {\n	\ntype ${g.entityName}Item = {\n	<%for(field in t.fields){%>\n		${field.name}: ${field.type},\n	<%}%>\n};\n\nconst stateRequest = async () => [\n  { label: \'全部\', value: \'all\' },\n  { label: \'未解决\', value: \'open\' },\n  { label: \'已解决\', value: \'closed\' },\n  { label: \'解决中\', value: \'processing\' },\n];\n\n\nconst columns: ProColumns<${g.entityName}Item>[] = [\n	// 查询表单\n	<%for(field in t.fields){%>\n		\n		<%if(field.isQuery==1){%>\n			{\n				title: \'${field.chinaName}\',\n				dataIndex: \'${field.name}\',\n				hideInTable:true,\n				<%if(field.showType==\'日期\'){%>\n					valueType:\"dateRange\",\n				 <%}%>\n				  <%if(field.showType==\'单选\'){%>\n					valueType:\"radio\",\n				 <%}%>\n				 <%if(field.showType==\'下拉\'){%>\n					valueType:\"select\",\n					request:stateRequest\n				 <%}%>\n			},\n		<%}%>\n	<%}%>\n	// 表格列表\n	<%for(field in t.fields){%>\n		\n		<%if(field.isShowList==1){%>\n			{\n				title: \'${field.chinaName}\',\n				dataIndex: \'${field.name}\',\n				hideInSearch:true,\n			},\n		<%}%>\n	<%}%>\n \n	{\n      title: \'操作\',\n      dataIndex: \'option\',\n      valueType: \'option\',\n      render: (text, record) => [\n        <a\n          key=\"edit\"\n          onClick={() => {\n            if (record?.id) {\n              handleModalVisible(true);\n              setModalTitle(\'编辑\');\n              setCurrentDisable(false);\n              setCurrentRow(record);\n              formRef?.current?.setFieldsValue(record);\n            }\n          }}\n        >\n          编辑\n        </a>,\n        <a\n          key=\"delete\"\n          onClick={async () => {\n            if (record?.id) {\n              handleDel(record.id);\n            }\n          }}\n        >\n          删除\n        </a>,\n      ],\n    },\n];\n\nconst [form] = Form.useForm<{ \n	<%for(field in t.fields){%>\n		${field.name}: ${field.type},\n	<%}%>\n }>();\nconst [createModalVisible, handleModalVisible] = useState<boolean>(false);\nconst [modalTitle, setModalTitle] = useState<string>(\'新增\');\nconst [showDrawer, setShowDrawer] = useState<boolean>(false);\nconst [currentRow, setCurrentRow] = useState<API.TenantItem>();\nconst actionRef = useRef<ActionType>();\nconst formRef = useRef<ProFormInstance>();\nconst params = { pageNo: 1,pageSize: 10,};\n \n\nreturn (\n	<PageContainer>\n	  <ProTable<${g.entityName}Item>\n		columns={columns}\n		actionRef={actionRef}\n		request={async () => {\n		  const msg = await list${g.entityName}(params);\n          return {\n            data: msg?.result.data || [],\n          };\n		}}\n		rowKey=\"key\"\n		dateFormatter=\"string\"\n		headerTitle=\"${g.name}\"\n		search={{\n		  defaultCollapsed: true,\n		  labelWidth: \'auto\',\n		  optionRender: (searchConfig, formProps, dom) => [\n			...dom.reverse(),\n			<Button key=\"out\" onClick={() => {}}>\n			  导出\n			</Button>,\n		  ],\n		}}\n		toolBarRender={() => [\n		  <Button key=\"primary\" type=\"primary\"\n			  onClick={() => {\n				  setShowDrawer(true);\n				}}\n		  >\n			<PlusOutlined />\n			新建\n		  </Button>,\n		]}\n	  />\n	  \n	  <DrawerForm<{\n		  <%for(field in t.fields){%>\n			${field.name}: ${field.type},\n		  <%}%>\n		}>\n      title=\"新建表单\"\n      form={form}\n      autoFocusFirstInput\n	  visible={showDrawer}\n      drawerProps={{\n        destroyOnClose: true,\n      }}\n      submitTimeout={2000}\n      onOpenChange= {\n		setShowDrawer\n      }\n	  onFinish={async (value) => {\n          let success = null;\n          if (modalTitle.indexOf(\'新增\') !== -1) {\n            success = await handleAdd(value as API.${g.entityName}Item);\n          } else {\n            const newData = Object.assign(currentRow, value);\n            setCurrentRow(newData);\n            success = await handleUpdate(newData);\n          }\n          if (success) {\n            handleModalVisible(false);\n            if (actionRef.current) {\n              actionRef.current.reload();\n            }\n          }\n        }}\n    >\n	 <%for(field in t.fields){%>\n		 \n			<%if(fieldLP.odd){%>\n				<ProForm.Group>\n			<%}%>\n			\n			<%if(field.showType==\'文本\'){%>\n				 <ProFormText\n					  rules={[\n					  <%if(field.notNull==1){%>\n							{\n							  required: true,\n							},\n					   <%}%>\n					  ]}\n					  name=\"${field.name}\"\n					  width=\"md\"\n					  label=\"${field.chinaName}\"\n					/>\n			 <%}%>\n			 <%if(field.showType==\'日期\'){%>\n				 <ProFormDatePicker name=\"${field.name}\" label=\"${field.chinaName}\"  />\n			 <%}%>\n			 <%if(field.showType==\'下拉\'){%>\n				 <CommonRadio name=\"${field.name}\" label=\"${field.chinaName}\" code=\"${field.dictName}\"\n						<%if(field.notNull==1){%>\n							required={true} \n					   <%}%>\n				/>\n			 <%}%>\n			 <%if(field.showType==\'文件\'){%>\n				 <ProFormText\n					  rules={[\n					  <%if(field.notNull==1){%>\n							{\n							  required: true,\n							},\n					   <%}%>\n					  ]}\n					  name=\"${field.name}\"\n					  width=\"md\"\n					  label=\"${field.chinaName}\"\n					/>\n			 <%}%>\n			\n			<%if(fieldLP.even || t.fields.~size==fieldLP.index){%>\n				</ProForm.Group>\n			<%}%>\n		 \n		  \n	  <%}%>\n    </DrawerForm>\n	\n	</PageContainer>\n	\n	\n);\n\n}\n\n\n\n\n//////////////////////// 接口调用开始  ////////////////////////////////\n\n\n\n// 新增数据接口调用\n  const handleAdd = async (fields: API.${g.entityName}Item) => {\n    const hide = message.loading(\'正在添加\');\n    try {\n      await add${g.entityName}({ ...fields });\n      message.success(\'新增成功！\');\n    } catch (error) {\n	  console.error(\"add error\", error);\n      message.error(\'新增失败！\');\n    } finally {\n	  hide();\n	}\n  };\n\n  // 更新数据接口调用\n  const handleUpdate = async (fields: API.${g.entityName}Item) => {\n    const hide = message.loading(\'正在修改\');\n    try {\n      await update${g.entityName}(fields);\n      message.success(\'修改成功！\'); \n    } catch (error) { \n	  console.error(\"edit error\", error);\n      message.error(\'修改失败！\'); \n    } finally {\n	  hide();\n	}\n  };\n\n  // 删除数据接口调用\n  const handleDel = async (id: string) => {\n    confirm({\n      title: \'提示\',\n      icon: <ExclamationCircleFilled />,\n      content: \'是否继续删除？\',\n      async onOk() {\n        const hide = message.loading(\'正在删除\');\n        if (!id) return true;\n        try {\n          await deleteTenant(id);\n          hide();\n          message.success(\'删除成功！\');\n          if (actionRef?.current) {\n            actionRef?.current.reload();\n          }\n          return true;\n        } catch (error) {\n          hide();\n          message.error(\'删除失败，请重试！\');\n          return false;\n        }\n      },\n      onCancel() {\n        console.log(\'Cancel\');\n      },\n    });\n  };\n//////////////////////// 接口调用开始  ////////////////////////////////\n\n//////////////////////// 接口定义开始  ///////////////////////////////////\n\nconst BASE_URL = URL_PREFIX.SYSTEM+\'/${g.underlineEntityName}s\';\n\n// 获取单个${g.name}\nexport async function get${g.entityName}(id: string) {\n  return request(BASE_URL+`/\\${id}`, {\n