INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (218, 213, 'package ${g.codePackage}.controller;\n\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport com.stcsm.core.common.result.CommonResult;\nimport ${g.codePackage}.entity.${g.entityName};\nimport ${g.codePackage}.service.${g.entityName}Service;\nimport org.springframework.web.bind.annotation.*;\n\nimport javax.annotation.Resource;\n\n/**\n * ${g.name}-控制层\n *\n * @author :  ${g.author}\n * @date : ${g.date}\n */\n@RestController\n@RequestMapping(\"/${g.underlineEntityName}s\")\npublic class ${g.entityName}Controller {\n\n    @Resource\n    private ${g.entityName}Service ${g.lowerEntityName}Service;\n\n\n    /**\n     * 分页查询\n     *\n     * @return 查询结果\n     */\n    @GetMapping\n    public CommonResult list(Page page) {\n        return CommonResult.success(${g.lowerEntityName}Service.page(page));\n    }\n\n    /**\n     * 通过ID查询单条数据\n     *\n     * @param id 主键\n     * @return 实例对象\n	 * @author :  ${g.author}\n	 * @date : ${g.date}\n     */\n    @GetMapping(\"{id}\")\n    public CommonResult getById(@PathVariable String id) {\n        return CommonResult.success(this.${g.lowerEntityName}Service.getById(id));\n    }\n\n\n    /**\n     * 新增数据\n     * @return 实例对象\n	 * @author :  ${g.author}\n	 * @date : ${g.date}\n     */\n    @PostMapping\n    public CommonResult add(@RequestBody UserAward userAward) {\n        return CommonResult.success(${g.lowerEntityName}Service.save(userAward));\n    }\n\n    /**\n     * 更新数据\n	 * @author :  ${g.author}\n	 * @date : ${g.date}\n     */\n    @PutMapping\n    public CommonResult update(@RequestBody UserAward userAward) {\n        return CommonResult.success(${g.lowerEntityName}Service.updateById(userAward));\n    }\n\n    /**\n     * 通过主键删除数据\n     * @param id 主键\n	 * @author :  ${g.author}\n	 * @date : ${g.date}\n     */\n    @DeleteMapping(\"{id}\")\n    public CommonResult deleteById(@PathVa