INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (236, 231, 'import { ExclamationCircleFilled, PlusOutlined } from \"@ant-design/icons\";\nimport type {\n  ActionType,\n  ProColumns,\n  ProFormInstance\n} from \"@ant-design/pro-components\";\nimport {\n  PageContainer,\n  ProFormText,\n  ProTable\n} from \"@ant-design/pro-components\";\nimport { DrawerForm, ProForm } from \"@ant-design/pro-form\";\nimport { Button, Form, message, Modal } from \"antd\";\nimport { useRef, useState } from \"react\";\nimport { extend } from \"umi-request\";\n\nconst BASE_URL = \'${g.codePackage}\'+\'/${g.underlineEntityName}s\';\n\nconst request = extend({\n  //prefix: \"/api\",\n  crossOrigin: true,\n  timeout: 60000\n});\n\nexport default () => {\ntype ${g.entityName}Item = {\n	<%for(field in t.fields){%>\n		\n		${field.name}: ${field.type},\n	<%}%>\n};\n\nconst columns: ProColumns<${g.entityName}Item>[] = [\n	// 查询表单\n	<%for(field in t.fields){%>\n		\n		<%if(field.isQuery==1){%>\n			{\n				title: \'${field.chinaName}\',\n				dataIndex: \'${field.name}\',\n				hideInTable:true,\n				<%if(field.showType==\'日期\'){%>\n					valueType:\"dateRange\",\n				 <%}%>\n				  <%if(field.showType==\'单选\'){%>\n					valueType:\"radio\",\n				 <%}%>\n				 <%if(field.showType==\'下拉\'){%>\n					valueType:\"select\",\n					request:stateRequest\n				 <%}%>\n			},\n		<%}%>\n	<%}%>\n	// 表格列表\n	<%for(field in t.fields){%>\n		\n		<%if(field.isShowList==1){%>\n			{\n				title: \'${field.chinaName}\',\n				dataIndex: \'${field.name}\',\n				hideInSearch:true,\n			},\n		<%}%>\n	<%}%>\n \n	{\n      title: \'操作\',\n      dataIndex: \'option\',\n      valueType: \'option\',\n      render: (text, record) => [\n        <a\n          key=\"edit\"\n          onClick={() => {\n            if (record?.id) {\n              handleModalVisible(true);\n              setModalTitle(\'编辑\');\n              setCurrentDisable(false);\n              setCurrentRow(record);