INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (268, 263, 'import { ExclamationCircleFilled, PlusOutlined } from \"@ant-design/icons\";\nimport type {\n  ActionType,\n  ProColumns,\n  ProFormInstance\n} from \"@ant-design/pro-components\";\nimport {\n  PageContainer,\n  ProFormText,\n  ProTable\n} from \"@ant-design/pro-components\";\nimport { DrawerForm, ProForm } from \"@ant-design/pro-form\";\nimport { Button, Form, message, Modal } from \"antd\";\nimport { useRef, useState } from \"react\";\nimport { extend } from \"umi-request\";\n\nconst BASE_URL = \'${g.codePackage}\'+\'/${g.underlineEntityName}s\';\n\nconst request = extend({\n  //prefix: \"/api\",\n  crossOrigin: true,\n  timeout: 60000\n});\n\nexport default () => {\ntype ${g.entityName}Item = {\n	<%for(field in t.fields){%>\n		\n		${field.name}: ${field.type},\n	<%}%>\n};\n\nconst columns: ProColumns<${g.entityName}Item>[] = [\n	// 查询表单\n	<%for(field in t.fields){%>\n		\n		<%if(field.isQuery==1){%>\n			{\n				title: \'${field.chinaName}\',\n				dataIndex: \'${field.name}\',\n				hideInTable:true,\n				<%if(field.showType==\'日期\'){%>\n					valueType:\"dateRange\",\n				 <%}%>\n				  <%if(field.showType==\'单选\'){%>\n					valueType:\"radio\",\n				 <%}%>\n				 <%if(field.showType==\'下拉\'){%>\n					valueType:\"select\",\n					request:stateRequest\n				 <%}%>\n			},\n		<%}%>\n	<%}%>\n	// 表格列表\n	<%for(field in t.fields){%>\n		\n		<%if(field.isShowList==1){%>\n			{\n				title: \'${field.chinaName}\',\n				dataIndex: \'${field.name}\',\n				hideInSearch:true,\n			},\n		<%}%>\n	<%}%>\n \n	{\n      title: \'操作\',\n      dataIndex: \'option\',\n      valueType: \'option\',\n      render: (text, record) => [\n        <a\n          key=\"edit\"\n          onClick={() => {\n            if (record?.id) {\n              handleModalVisible(true);\n              setModalTitle(\'编辑\');\n              setCurrentDisable(false);\n              setCurrentRow(record);\n              formRef?.current?.setFieldsValue(record);\n            }\n          }}\n        >\n          编辑\n        </a>,\n        <a\n          key=\"delete\"\n          onClick={async () => {\n            if (record?.id) {\n              handleDel(record.id);\n            }\n          }}\n        >\n          删除\n        </a>,\n      ],\n    },\n];\n\n\nconst [form] = Form.useForm<{ \n	<%for(field in t.formFields){%>\n	   /** ${field.chinaName} */\n	   ${field.name}: ${field.type},\n	<%}%>\n }>();\n \nconst [createModalVisible, handleModalVisible] = useState<boolean>(false);\nconst [modalTitle, setModalTitle] = useState<string>(\'新增\');\nconst [showDrawer, setShowDrawer] = useState<boolean>(false);\nconst [currentRow, setCurrentRow] = useState<API.TenantItem>();\nconst actionRef = useRef<ActionType>();\nconst formRef = useRef<ProFormInstance>();\nconst params = { pageNo: 1,pageSize: 10,};\n \n\nreturn (\n	<PageContainer>\n	  <ProTable<${g.entityName}Item>\n		columns={columns}\n		actionRef={actionRef}\n		request={async () => {\n		  const msg = await list${g.entityName}(params);\n          return {\n            data: msg?.result.data || [],\n          };\n		}}\n		rowKey=\"key\"\n		dateFormatter=\"string\"\n		headerTitle=\"${g.name}\"\n		search={{\n		  defaultCollapsed: true,\n		  labelWidth: \'auto\',\n		  optionRender: (searchConfig, formProps, dom) => [\n			...dom.reverse(),\n			<Button key=\"out\" onClick={() => {}}>\n			  导出\n			</Button>,\n		  ],\n		}}\n		toolBarRender={() => [\n		  <Button key=\"primary\" type=\"primary\"\n			  onClick={() => {\n				  setShowDrawer(true);\n				}}\n		  >\n			<PlusOutlined />\n			新建\n		  </Button>,\n		]}\n	  />\n	  \n	  <DrawerForm<{\n		  <%for(field in t.fields){%>\n		   <%if(field.name != \'createdAt\' && field.name != \'modifiedAt\' && field.name != \'id\' && field.name != \'createUserId\' && field.name != \'createUser\' && field.name != \'updateUser\' && field.name != \'updateUserId\' && field.name != \'version\' && field.name != \'delFlag\' && field.name != \'createDeptId\' ){%>\n			<%if(field.isShowAdd == 1  ){%>\n				${field.name}: ${field.type},\n			<%}%>\n			<%}%>\n		  <%}%>\n		}>\n      title=\"新建表单\"\n      form={form}\n      autoFocusFirstInput\n	  visible={showDrawer}\n      drawerProps={{\n        destroyOnClose: true,\n      }}\n      submitTimeout={2000}\n      onOpenChange= {\n		setShowDrawer\n      }\n	  onFinish={async (value) => {\n          let success = null;\n          if (modalTitle.indexOf(\'新增\') !== -1) {\n            success = await handleAdd(value as API.${g.entityName}Item);\n          } else {\n            const newData = Object.assign(currentRow, value);\n            setCurrentRow(newData);\n            success = await handleUpdate(newData);\n          }\n          if (success) {\n            handleModalVisible(false);\n            if (actionRef.current) {\n              actionRef.current.reload();\n            }\n          }\n        }}\n    >\n	 <%for(field in t.formFields){%>\n		 \n		 <%if(fieldLP.odd){%>\n				<ProForm.Group>\n			<%}%>\n			\n			<%if(field.showType==\'文本\'){%>\n				 <ProFormText\n					  rules={[\n					  <%if(field.notNull==1){%>\n							{\n							  required: true,\n							},\n					   <%}%>\n					  ]}\n					  name=\"${field.name}\"\n					  width=\"md\"\n					  label=\"${field.chinaName}\"\n					/>\n			 <%}%>\n			 <%if(field.showType==\'日期\'){%>\n				 <ProFormDatePicker name=\"${field.name}\" label=\"${field.chinaName}\"  />\n			 <%}%>\n			 <%if(field.showType==\'下拉\'){%>\n				 <CommonRadio name=\"${field.name}\" label=\"${field.chinaName}\" code=\"${field.dictName}\"\n						<%if(field.notNull==1){%>\n							required={true} \n					   <%}%>\n				/>\n			 <%}%>\n			 <%if(field.showType==\'文件\'){%>\n				 <ProFormText\n					  rules={[\n					  <%if(field.notNull==1){%>\n							{\n							  required: true,\n							},\n					   <%}%>\n					  ]}\n					  name=\"${field.name}\"\n					  width=\"md\"\n					  label=\"${field.chinaName}\"\n					/>\n			 <%}%>\n			\n			<%if(fieldLP.even || t.fields.~size==fieldLP.index){%>\n				</ProForm.Group>\n			<%}%>\n		  \n	  <%}%>\n    </DrawerForm>\n	</PageContainer>\n);\n}\n\n\n\n//////////////////////// 接口调用开始  ////////////////////////////////\n\n\n\n// 新增数据接口调用\n  const handleAdd = async (fields: API.${g.entityName}Item) => {\n    const hide = message.loading(\'正在添加\');\n    try {\n      await add${g.entityName}({ ...fields });\n      message.success(\'新增成功！\');\n    } catch (error) {\n	  console.error(\"add error\", error);\n      message.error(\'新增失败！\');\n    } finally {\n	  hide();\n	}\n  };\n\n  // 更新数据接口调用\n  const handleUpdate = async (fields: API.${g.entityName}Item) => {\n    const hide = message.loading(\'正在修改\');\n    try {\n      await update${g.entityName}(fields);\n      message.success(\'修改成功！\'); \n    } catch (error) { \n	  console.error(\"edit error\", error);\n      message.error(\'修改失败！\'); \n    } finally {\n	  hide();\n	}\n  };\n\n  // 删除数据接口调用\n  const handleDel = async (id: string) => {\n    confirm({\n      title: \'提示\',\n      icon: <ExclamationCircleFilled />,\n      content: \'是否继续删除？\',\n      async onOk() {\n        const hide = message.loading(\'正在删除\');\n        if (!id) return true;\n        try {\n          await deleteTenant(id);\n          hide();\n          message.success(\'删除成功！\');\n          if (actionRef?.current) {\n            actionRef?.current.reload();\n          }\n          return true;\n        } catch (error) {\n          hide();\n          message.error(\'删除失败，请重试！\');\n          return false;\n        }\n      },\n      onCancel() {\n        console.log(\'Cancel\');\n      },\n    });\n  };\n//////////////////////// 接口调用开始  ////////////////////////////////\n\n//////////////////////// 接口定义开始  ///////////////////////////////////\n\n\n\n// 获取单个${g.name}\nexport async function get${g.entityName}(id: string) {\n  return request(BASE_URL+`/\\${id}`, {\n    method: \'GET\',\n  });\n}\n\n\n// 获取${g.name}列表\nexport async function list${g.entityName}(params: any) {\n  return request(BASE_URL, {\n    method: \'GET\',\n    data: params,\n  });\n}\n\n// 新增${g.name}\nexport async function add${g.entityName}(params: any) {\n  return request(BASE_URL, {\n    method: \'POST\',\n    data: params,\n  });\n}\n\n// 修改${g.name}\nexport async function update${g.entityName}(params: any) {\n  return request(BASE_URL, {\n    method: \'PUT\',\n    data: params,\n  });\n}\n\n\n// 删除${g.name}\nexport async function delete${g.entityName}(id: string) {\n  return request(BASE_URL+`/\\${id}`, {\n    method: \'DELETE\',\n  });\n}\n\n\n\n//////////////////////// 接口定义结束  ///////////////////////////////////', NULL, '2023-05-09 11:07:39', NULL);
INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (269, 264, '<template>\n  <el-drawer\n    :title=\"info.id ? \'编辑${g.name}\' : \'新增${g.name}\'\"\n    :visible.sync=\"visible\"\n    size=\"1072px\"\n    :before-close=\"handleClose\">\n    <el-form :model=\"${g.lowerEntityName}\" :rules=\"rules\" ref=\"${g.lowerEntityName}\" label-width=\"100px\" class=\"equipmentForm\" label-position=\"top\">\n      <el-row :gutter=\"48\">\n	  <%for(field in t.fields){%>\n	  \n	   <%if(field.isShowAdd==1){%>\n				 \n	      <%if(field.showType==\'单选\'){%>\n				<el-col :span=\"12\">\n					<el-form-item label=\"${field.chinaName}\" prop=\"${field.name}\">\n					  <el-radio-group v-model=\"${g.lowerEntityName}.${field.name}\" @change=\"classChange\">\n						<el-radio label=\"A\">选项A</el-radio>\n						<el-radio label=\"B\">选项B</el-radio>\n					  </el-radio-group>\n					</el-form-item>\n				   </el-col>\n		<%}else{%>\n				 <el-col :span=\"24\">\n				  <el-form-item label=\"${field.chinaName}\" prop=\"${field.name}\">\n					<el-input v-model=\"${g.lowerEntityName}.${field.name}\"></el-input>\n				  </el-form-item>\n				</el-col> \n		<%}%><%}%> <%}%>\n      </el-row>\n    </el-form>\n    <div class=\"actions\">\n      <el-button type=\"primary\" :loading=\"saveLoading\" @click=\"handleSaveCheck\" class=\"save\" size=\"small\">确认</el-button>\n    </div>\n  </el-drawer>\n</template>\n\n<script>\nexport default {\n  data(){\n    return{\n      saveLoading:false,\n      fileList:[],\n      visible:false,\n      classCode:\'A\',\n      equipmentCode:\'1\',\n      info:{},\n      ${g.lowerEntityName}:{},\n      rules: {\n	  <%for(field in t.fields){%>\n	    <%if(field.viewVerification==1){%>\n			 ${field.name}: [\n				{ required: true, message: \'请输入\', trigger: \'change\' }\n			 ],\n		<%}%>\n	  <%}%>\n      }\n    }\n  },\n  computed: {\n    isTenThousand () {\n      return this.equipmentCode === \'2\'\n    }\n  },\n  methods:{\n    handleSaveCheck(){\n\n    },\n    classChange (item) {\n      this.classCode = item\n    },\n    equipmentTypeChange(item){\n      this.equipmentCode = item\n    },\n    init(){\n      this.visible = true\n    },\n    handleClose(done){\n      done();\n    },\n    submitForm(formName) {\n        this.$refs[formName].validate((valid) => {\n          if (valid) {\n            alert(\'submit!\');\n          } else {\n            console.log(\'error submit!!\');\n            return false;\n          }\n        });\n      },\n  }\n}\n</script>\n\n<style lang=\"less\" scoped>\n.equipmentForm{\n  padding: 24px;\n  margin-bottom: 20px;\n}\n.actions{\n  text-align: right;\n  padding: 10px 16px;\n  border-top: 1px solid #e9e9e9;\n  position: absolute;\n  left: 0;\n  bottom: 0;\n  z-index: 100;\n  background-color: #ffffff;\n  width: 100%\n}\n</style>', 'Java', '2023-05-09 13:08:56', NULL);
