INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (350, 345, '/**\n * ${g.name}管理初始化\n */\nvar ${g.entityName} = {\n    id: \"${g.entityName}Table\",	//表格id\n        seItem: null,		//选中的条目\n        table: null,\n        layerIndex: -1\n};\n\nvar requestBaseUrl = \"${g.codePackage}\"+\"/${strutil.toLowerCase(g.entityName)}\";\n/**\n * 初始化表格的列\n */\n${g.entityName}.initColumn = function () {\n    return [\n            {field: \'selectItem\', radio: true},\n        <%for(field in t.fields){%>\n        <%if(field.isShowList ==2){%>\n        {title: \'${field.chinaName}\', field: \'${field.name}\', align: \'center\', valign: \'middle\'},\n        <%}}%>\n    ];\n};\n\n/**\n * 检查是否选中\n */\n${g.entityName}.check = function () {\n    var selected = $(\'#\' + this.id).bootstrapTable(\'getSelections\');\n    if(selected.length == 0){\n        Feng.info(\"请先选中表格中的某一记录！\");\n        return false;\n    }else{\n        ${g.entityName}.seItem = selected[0];\n        return true;\n    }\n};\n\n/**\n * 点击添加${g.name}\n */\n${g.entityName}.openAdd${g.entityName} = function () {\n    var index = layer.open({\n        type: 2,\n        title: \'添加${g.name}\',\n        area: [\'800px\', \'420px\'], //宽高\n        fix: false, //不固定\n        maxmin: true,\n        content: Feng.ctxPath + requestBaseUrl +\'/goto_add\'\n    });\n    this.layerIndex = index;\n};\n\n/**\n * 打开查看${g.name}详情\n */\n${g.entityName}.open${g.entityName}Detail = function () {\n    if (this.check()) {\n        var index = layer.open({\n            type: 2,\n            title: \'${g.name}详情\',\n            area: [\'800px\', \'420px\'], //宽高\n            fix: false, //不固定\n            maxmin: true,\n            content: Feng.ctxPath +requestBaseUrl + \'/goto_update/\' + ${g.entityName}.seItem.id\n    });\n        this.layerIndex = index;\n    }\n};\n\n/**\n * 删除${g.name}\n */\n${g.entityName}.delete = function () {\n    if (this.check()) {\n        var ajax = new $ax(Feng.ctxPath + requestBaseUrl +\"/delete\", function (data) {\n            Feng.success(\"删除成功!\");\n            ${g.entityName}.table.refresh();\n        }, function (data) {\n            Feng.error(\"删除失败!\" + data.responseJSON.message + \"!\");\n        });\n        ajax.set(\"id\",this.seItem.id);\n        ajax.start();\n    }\n};\n\n${g.entityName}.formParams = function() {\n    var queryData = {};\n    return queryData;\n};\n\n/**\n * 查询${g.name}列表\n */\n${g.entityName}.search = function () {\n    var queryData = {};\n    <%for(field in t.fields){%>\n    <%if(field.isQuery == 1){%>\n        queryData[\'${field.name}\'] = $(\"#${field.name}\").val();\n    <%}}%>\n\n    ${g.entityName}.table.refresh({query: queryData});\n};\n\n$(function () {\n    var defaultColumns = ${g.entityName}.initColumn();\n    var table = new BSTable(${g.entityName}.id, requestBaseUrl +\"/list\", defaultColumns );\n    table.setPaginationType(\"server\");\n    table.setQueryParams(${g.entityName}.formParams());\n    ${g.entityName}.table = table.init();\n});\n', 'Frontend', '2023-09-17 17:47:53', NULL);
INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (351, 346, '/**\n * 初始化${g.name}详情对话框\n */\nvar ${g.entityName}InfoDlg = {\n    ${strutil.toUpperCase(strutil.subStringTo (g.entityName,0,1))}${strutil.subString  (g.entityName,1)}InfoData : {},\n    // 校验条件\n    validateFields: {\n    }\n};\n\nvar requestBaseUrl = \"${g.codePackage}\"+\"/${strutil.toLowerCase(g.entityName)}\";\n\n/**\n * 清除数据\n */\n${g.entityName}InfoDlg.clearData = function() {\n    this.${strutil.toUpperCase(strutil.subStringTo (g.entityName,0,1))}${strutil.subString  (g.entityName,1)}InfoData = {};\n}\n\n/**\n * 设置对话框中的数据\n *\n * @param key 数据的名称\n * @param val 数据的具体值\n */\n${g.entityName}InfoDlg.set = function(key, val) {\n    this.${strutil.toUpperCase(strutil.subStringTo (g.entityName,0,1))}${strutil.subString  (g.entityName,1)}InfoData[key] = (typeof val == \"undefined\") ? $(\"#\" + key).val() : val;\n    return this;\n}\n\n/**\n * 设置对话框中的数据\n *\n * @param key 数据的名称\n * @param val 数据的具体值\n */\n${g.entityName}InfoDlg.get = function(key) {\n    return $(\"#\" + key).val();\n}\n\n/**\n * 关闭此对话框\n */\n${g.entityName}InfoDlg.close = function() {\n    parent.layer.close(window.parent.${g.entityName}.layerIndex);\n}\n\n/**\n * 收集数据\n */\n${g.entityName}InfoDlg.collectData = function() {\n    this<%for(field in t.fields){%>.set(\'${field.name}\')<%}%>;\n}\n\n/**\n * 验证数据是否为空\n */\n${g.entityName}InfoDlg.validate = function () {\n    $(\'#${g.lowerEntityName}Form\').data(\"bootstrapValidator\").resetForm();\n    $(\'#${g.lowerEntityName}Form\').bootstrapValidator(\'validate\');\n    return $(\"#${g.lowerEntityName}Form\").data(\'bootstrapValidator\').isValid();\n}\n\n\n\n/**\n * 提交添加\n */\n${g.entityName}InfoDlg.addSubmit = function() {\n\n    this.clearData();\n    this.collectData();\n    if (!this.validate()) {\n        return;\n    }\n    //提交信息\n    var ajax = new $ax(Feng.ctxPath  +requestBaseUrl +\"/add\", function(data){\n        Feng.success(\"添加成功!\");\n        window.parent.${g.entityName}.table.refresh();\n        ${g.entityName}InfoDlg.close();\n    },function(data){\n        Feng.error(\"添加失败!\" + data.responseJSON.message + \"!\");\n    });\n    ajax.set(this.${strutil.toUpperCase(strutil.subStringTo (g.entityName,0,1))}${strutil.subString  (g.entityName,1)}InfoData);\n    ajax.start();\n}\n\n/**\n * 提交修改\n */\n${g.entityName}InfoDlg.editSubmit = function() {\n\n    this.clearData();\n    this.collectData();\n    if (!this.validate()) {\n        return;\n    }\n    //提交信息\n    var ajax = new $ax(Feng.ctxPath  +requestBaseUrl + \"/update\", function(data){\n        Feng.success(\"修改成功!\");\n        window.parent.${g.entityName}.table.refresh();\n        ${g.entityName}InfoDlg.close();\n    },function(data){\n        Feng.error(\"修改失败!\" + data.responseJSON.message + \"!\");\n    });\n    ajax.set(this.${strutil.toUpperCase(strutil.subStringTo (g.entityName,0,1))}${strutil.subString  (g.entityName,1)}InfoData);\n    ajax.start();\n}\n\n$(function() {\n    Feng.initValidator(\"${g.lowerEntityName}Form\", ${g.entityName}InfoDlg.validateFields);\n});\n', 'Frontend', '2023-09-17 17:48:21', NULL);
