INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (353, 348, 'package ${g.codePackage}.controller;\n\nimport com.baomidou.mybatisplus.extension.plugins.pagination.Page;\nimport com.stcsm.core.common.result.CommonResult;\nimport ${g.codePackage}.entity.${g.entityName};\nimport ${g.codePackage}.service.${g.entityName}Service;\nimport org.springframework.web.bind.annotation.*;\n\nimport javax.annotation.Resource;\n\n/**\n * ${g.name}-控制层\n *\n * @author :  ${g.author}\n * @date : ${g.date}\n */\n@Controller\n@RequestMapping(\"/${g.underlineEntityName}\")\npublic class ${g.entityName}Controller {\n\n    @Resource\n    private ${g.entityName}Service ${g.lowerEntityName}Service;\n\n    private String PREFIX = \"/code/gen/\";\n\n\n    /**\n     * 跳转到首页\n     */\n    @RequestMapping(\"\")\n    public String index(Model modelMap) {\n        return PREFIX + \"index.html\";\n    }\n\n     /**\n     * 获取列表\n     */\n    @RequestMapping(value = \"/list\")\n    @ResponseBody\n    public Object list(${g.entityName}Model model, HttpServletRequest req, HttpServletResponse res) {\n        Page<${g.entityName}Model> page = new PageFactory<${g.entityName}Model>().defaultPage();\n        model.setUserId(ShiroKit.getUser().id);\n        page.setRecords(tableBaseFieldService.selectPage(page, model, new EntityWrapper<${g.entityName}Model>()));\n        BeanKeyConvert.systemUserNameConvert(page.getRecords());\n        return super.packForBT(page);\n    }\n\n    @BussinessLog(value = \"新增\", key = \"tableName\")\n    @RequestMapping(value = \"/add\")\n    @Permission\n    @ResponseBody\n    public Object add(@RequestBody ${g.entityName}Model model) {\n        ${g.lowerEntityName}Service.insert(model,ShiroKit.getUser().getId());\n        return SUCCESS_TIP;\n    }\n\n\n    @BussinessLog(value = \"删除\", key = \"id\")\n    @RequestMapping(value = \"/delete\")\n    @ResponseBody\n    public Object delete(Integer id) {\n        ${g.lowerEntityName}Service.deleteById(id);\n        return SUCCESS_TIP;\n    }\n\n\n    @BussinessLog(value = \"修改\", key = \"id\")\n    @RequestMapping(value = \"/update\")\n    @ResponseBody\n    public Object update(@RequestBody ${g.entityName}Model model) {\n        if (ToolUtil.isOneEmpty(model.getId())) {\n            throw new BussinessException(BizExceptionEnum.REQUEST_NULL);\n        }\n        ${g.lowerEntityName}Service.updateById(model,ShiroKit.getUser().getId());\n        return SUCCESS_TIP;\n    }\n\n    /**\n     * 详情\n     */\n    @RequestMapping(value = \"/detail\")\n    @ResponseBody\n    public Object detail(${g.entityName}Model model) {\n        return ${g.lowerEntityName}Service.selectOne(model);\n    }\n\n}', NULL, '2023-09-17 19:04:58', NULL);
