INSERT INTO `t_code_template_file` (`id`, `template_id`, `file`, `file_type`, `CRT_TIME`, `MDF_TIME`) VALUES (375, 370, 'package ${g.codePackage}.controller;\n\nimport com.baomidou.mybatisplus.mapper.EntityWrapper;\nimport com.baomidou.mybatisplus.plugins.Page;\nimport com.baomidou.mybatisplus.plugins.pagination.Pagination;\nimport ${g.codePackage}.model.${g.entityName};\nimport ${g.codePackage}.service.${g.entityName}Service;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\nimport org.springframework.beans.factory.annotation.Autowired;\nimport org.springframework.stereotype.Controller;\nimport org.springframework.ui.Model;\nimport org.springframework.web.bind.annotation.PathVariable;\nimport org.springframework.web.bind.annotation.RequestMapping;\nimport org.springframework.web.bind.annotation.RequestParam;\nimport org.springframework.web.bind.annotation.ResponseBody;\n\nimport javax.annotation.Resource;\nimport java.util.List;\nimport java.util.Map;\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\n/**\n * ${g.name}控制器\n *\n * @author ${g.author}\n * @Date ${g.date}\n */\n@Controller\n@RequestMapping(\"/${strutil.toLowerCase(g.entityName)}\")\npublic class ${g.entityName}Controller extends BaseController {\n\n    private static final Logger LOGGER = LoggerFactory.getLogger(${g.entityName}Controller.class);\n\n    private String PREFIX = \"/biz/${strutil.toLowerCase(g.entityName)}/\";\n\n    @Autowired\n    private ${g.entityName}Service ${g.lowerEntityName}Service;\n\n    /**\n     * 跳转到首页\n     */\n    @RequestMapping(\"\")\n    public String index() {\n        return PREFIX + \"${strutil.toLowerCase(g.entityName)}.html\";\n    }\n\n    /**\n     * 跳转到添加\n     */\n    @RequestMapping(\"/goto_add\")\n    public String gotoAdd() {\n        return PREFIX + \"${strutil.toLowerCase(g.entityName)}_add.html\";\n    }\n\n    /**\n     * 跳转到修改\n     */\n    @RequestMapping(\"/goto_update/{id}\")\n    public String gotoUpdate(@PathVariable Integer id,Model model) {\n        model.addAttribute(\"${strutil.toLowerCase(g.entityName)}\", ${g.lowerEntityName}Service.selectById(id));\n        return PREFIX + \"${strutil.toLowerCase(g.entityName)}_edit.html\";\n    }\n\n    /**\n     * 获取列表\n     */\n    @RequestMapping(value = \"/list\")\n    @ResponseBody\n    public Object list(${g.entityName} model,HttpServletRequest req, HttpServletResponse res) {\n         Page<Module> page = new PageFactory<Module>().defaultPage();\n        moduleService.selectPage(page).getRecords();\n        page.setRecords(moduleService.selectPage(page).getRecords());\n        return super.packForBT(page);\n    }\n\n\n    @RequestMapping(value = \"/add\")\n    \n    @ResponseBody\n    public Object add(${g.entityName} model) {\n        ${g.lowerEntityName}Service.insert(model);\n        return SUCCESS_TIP;\n    }\n\n\n    @RequestMapping(value = \"/delete\")\n    \n    @ResponseBody\n    public Object delete(Integer id) {\n        ${g.lowerEntityName}Service.deleteById(id);\n        return SUCCESS_TIP;\n    }\n\n\n    @RequestMapping(value = \"/update\")\n    \n    @ResponseBody\n    public Object update(${g.entityName} model) {\n        if (ToolUtil.isOneEmpty(model.getId())) {\n        throw new BussinessException(BizExceptionEnum.REQUEST_NULL);\n        }\n        ${g.lowerEntityName}Service.updateById(model);\n        return SUCCESS_TIP;\n    }\n\n    @RequestMapping(value = \"/detail\")\n    @ResponseBody\n    public Object detail(Module model) {\n        return moduleService.selectById(model);\n    }\n}', 'Backend', '2023-09-18 17:05:38', NULL);
